 $(function(){
      $('.btn_nav').click(function(){
        $(this).toggleClass('on');
        $('.nav_wrap').toggleClass('open');
      });
      
       //lnb
    $(window).resize(function(){
        if($(window).width() < 1024){
            $('.sub').hide(); 
            $('.nav_inner .lnb li a').click(function(){ //대메뉴 클릭시
                $('.sub:not(:animated)').slideUp(); //서브닫고
                $(this).next().slideDown(); //서브열기
            })
        } else {
            $('.sub').show(); 
            $('.nav_inner .lnb li a').off('click') //모바일클릭해제
            .mouseenter(function(){
                $(this).addClass('on');
            }).mouseleave(function(){
                $(this).removeClass('on');
            });
            $('.nav_inner').mouseenter(function(){
                $(this).addClass('on');
            }).mouseleave(function(){
                $(this).removeClass('on');
            });
        }
        
      }).resize();
    
    });


    $(function(){
        
    //scroll
    $(window).scroll(function(){
		var wds = $(this);
		
    animation(wds,$('.brand_inner > b'));
    animation(wds,$('.brand_inner > div'));
    animation(wds,$('.brand_inner > p'));
    animation(wds,$('.brand_inner > a'));
    animation(wds,$('.beside > div'));  
		animation(wds,$('.mall_inner > div'));
		animation(wds,$('.store_inner > h2'));
		animation(wds,$('.store_inner > p'));
		animation(wds,$('.store_inner > a'));
		
	})
	
	// animation function
	
	 function animation(ws,pr) {
        if(ws.scrollTop() >= pr.offset().top - ws.height()){
            pr.addClass('ani');
        } else {
            pr.removeClass('ani');
        }
    }
	});